// import { BigNumber } from 'ethers';
import { useMemo, useEffect, useState } from 'react';
import useJsonRpcProvider from '../web3/useJsonRpcProvider';

enum GuildImplementationType {
  SnapshotRepERC20Guild = 'SnapshotRepERC20Guild',
  DXDGuild = 'DXDGuild',
  ERC20Guild = 'ERC20Guild',
  IERC20Guild = 'IERC20Guild',
}

export default function useGuildImplementationType(guildAddress) {
  const [guildBytecode, setGuildBytecode] = useState<string>('');
  const provider = useJsonRpcProvider();

  useEffect(() => {
    const getBytecode = async () => {
      // get current guild bytecode;
      const bytecode = await provider.getCode(guildAddress);
      setGuildBytecode(bytecode);
    };
    getBytecode();
  }, [guildAddress, provider]);

  const currentImplementation = useMemo(() => {
    if (!guildBytecode) return '';

    // compare current guild bytecode with other guilds implementation bytecode;
    const match = Object.entries(deployedBytecodes).find(([_, bytecode]) => {
      return guildBytecode === bytecode;
    });

    return match
      ? GuildImplementationType[match[0]]
      : GuildImplementationType.ERC20Guild;
  }, [guildBytecode]);

  return currentImplementation;
}

const deployedSnapshotRepErc20GuildBytecode =
  '';

const deployedDXDGuildBytecode =
  '';

const deployedBytecodes = {
  [GuildImplementationType.SnapshotRepERC20Guild]:
    deployedSnapshotRepErc20GuildBytecode,
  [GuildImplementationType.DXDGuild]: deployedDXDGuildBytecode,
};
